import {FETCH_PRODUCTS, FILTER_PRODUCTS_BY_SIZE} from "../actions/types";

const initialState = { items: [] };
export default function(state=initialState, action){
  switch(action.type){
    case FETCH_PRODUCTS:
      return {...state, items: action.payload};
    case FILTER_PRODUCTS_BY_SIZE:
      return {...state, items: action.payload};
    default:
      return state;
  }
}
