import React, { Component } from 'react';
import './App.css';
import Products from './components/Products';
import Filter from './components/Filter';
import Basket from './components/Basket';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {products:[], filteredProducts:[], size: '', sort: '', cartItems: []};
    this.handleChangeSort = this.handleChangeSort.bind(this);
    this.handleChangeSize = this.handleChangeSize.bind(this);
    this.handleAddToCart = this.handleAddToCart.bind(this);
    this.handleRemoveFromCart = this.handleRemoveFromCart(this);
  }
  componentDidMount(){
    fetch("http://localhost:8000/products").then(res => res.json())
    .then(data => this.setState({
      products: data,
      filteredProducts: data
    }));
    if(localStorage.getItem('cartItems')){
      this.setState({cartItems: JSON.parse(localStorage.getItem('cartItems'))});
    }
  }
  handleChangeSort(e){
    console.log('products',this.state.products)
    this.setState({sort: e.target.value});
    this.listProducts();
  }
  handleChangeSize(e){
    this.setState({size: e.target.value});
    this.listProducts();
  }
  listProducts(){
    this.setState(state => {
      console.log(state.products)
      console.log('sort',state.sort)
      console.log('size',state.size)
      if (state.sort !== ''){
        console.log('boolean', state.sort==='lowest' )
        state.products.sort((a,b) => (state.sort==='lowest')? 
        (a.price - b.price) 
        : (b.price - a.price))
      } else{
        state.products.sort((a,b) => (b.id - a.id));
      }
      if (state.size !== ''){
        return {
          filteredProducts: state.products.filter(a => a.availableSizes.indexOf(state.size.toUpperCase())>=0)
        };
      }
      return {filteredProducts: state.products};
    })
  }
  handleAddToCart = (e, product) => {
    this.setState(state => {
      const cartItems = state.cartItems;
      let productAlreadyInCart = false;

      cartItems.forEach(item => {
        console.log('item',item)
        if(item.id === product.id){
          productAlreadyInCart = true;
          console.log('count1', item.count)
          item.count += 1;
          console.log('count2', item.count)
        }
      });
      console.log('productAlreadyInCart', productAlreadyInCart)
      if (!productAlreadyInCart){
        cartItems.push({ ...product, count: 1 });
      }
      localStorage.setItem("cartItems", JSON.stringify(cartItems));
      return { cartItems: cartItems };
    })
  }
  handleRemoveFromCart = (e, item) => {
    this.setState(state => {
      const cartItems = state.cartItems.filter(elm => elm.id !== item.id);
      localStorage.setItem('cartItem', JSON.stringify(cartItems));
      return { cartItems: cartItems };
    });
  }
  render() {
    return(
      <div className="Container">
      <h1>Ecommerce Shopping Cart Application</h1>
      <hr/>
      <div className="row">
        <div className="col-md-8">
          <Filter 
            size={this.state.size} 
            sort={this.state.sort} 
            handleChangeSize={this.handleChangeSize} 
            handleChangeSort={this.handleChangeSort} 
            count={this.state.filteredProducts.length} 
          />
          <hr/>
          <Products products={this.state.filteredProducts} handleAddToCart={this.handleAddToCart}/>
        </div>
        <div className="col-md-4">
          <Basket cartItems = {this.state.cartItems} handleRemoveFromCart={this.handleRemoveFromCart}/>
        </div>
      </div>
      </div>
    );
  }
}

export default App;
